document.addEventListener("DOMContentLoaded", () => {
  const newsContent = document.getElementById("news-container");
  const newsItems = document.querySelectorAll(".news-item");
  const downloadBtn = document.getElementById("download-pdf");

  // Affichage du texte au clic sur le sommaire
  newsItems.forEach(item => {
    item.addEventListener("click", () => {
      const key = item.textContent.split(":")[1].trim();

      // On insère le texte UNE seule fois
      newsContent.innerHTML = newsData[key] || "<p>Texte introuvable</p>";

      // On affiche le bouton PDF
      downloadBtn.style.display = "inline-block";

      newsContent.classList.add("visible");
      newsContent.scrollIntoView({ behavior: "smooth" });
      
    });
  });

  // Génération du PDF
  downloadBtn.addEventListener("click", () => {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    doc.setFont("times");
    doc.setFontSize(12);
    const lineHeight = 12 * 1; // Interligne

    const elements = newsContent.querySelectorAll(".neon, .chelsea-text p");
    let cursorY = 10;

    elements.forEach(el => {
      const text = el.innerText.trim();
      if (!text) return;

      // Styles
      if (el.classList.contains("bold")) doc.setFont("times", "bold");
      else if (el.classList.contains("italic")) doc.setFont("times", "italic");
      else doc.setFont("times", "normal");

      // Alignement
      let align = "left";
      if (el.classList.contains("align-center")) align = "center";
      else if (el.classList.contains("align-right")) align = "right";
      // Découper le texte en lignes pour le PDF
      const pageWidth = doc.internal.pageSize.getWidth();
      const margin = 10;
      const lines = doc.splitTextToSize(text, pageWidth - margin * 2);

      lines.forEach(line => {
        let x = margin;
        if (align === "center") x = pageWidth / 2;
        else if (align === "right") x = pageWidth - margin;

        doc.text(line, x, cursorY, { align });
        cursorY += lineHeight;

        if (cursorY > doc.internal.pageSize.getHeight() - 10) {
          doc.addPage();
          cursorY = 10;
        }
      });
    });

    // Nom du PDF avec le titre
    const firstTitle = newsContent.querySelector(".neon")?.innerText || "texte";
    doc.save(`${firstTitle}.pdf`);
  });
});

document.addEventListener("DOMContentLoaded", () => {
  // On parcourt toutes les cartes
  document.querySelectorAll(".text").forEach(text => {

    let ul = null;

    // On parcourt tous les enfants directs de la card
    Array.from(text.children).forEach(el => {
      const text = el.textContent.trim();

      // Si la ligne commence par un "-"
      if (text.startsWith("-")) {

        // Si on n'a pas encore créé de <ul>, on le fait
        if (!ul) {
          ul = document.createElement("ul");
          text.insertBefore(ul, el); // on place le <ul> avant l'élément actuel
        }

        // Crée un <li> avec le texte sans le "-"
        const li = document.createElement("li");
        li.textContent = text.substring(1).trim();

        // Ajoute le <li> au <ul>
        ul.appendChild(li);

        // Supprime l'élément original
        el.remove();

      } else {
        // Si ce n'est pas une ligne avec "-", on réinitialise ul
        ul = null;
      }
    });
  });
});

